package irvine.oeis.a094;

import irvine.math.z.Z;
import irvine.oeis.triangle.Triangle;

/**
 * A094615 Triangular array T of numbers generated by these rules: 1 is in T; and if x is in T, then 2x+1 and 3x+2 are in T.
 * T(n,k) = -1 + 2^(n+1-k)*3^k
 * @author Georg Fischer
 */
public class A094615 extends Triangle {

  @Override
  public Z compute(final int n, final int k) {
    return Z.ONE.shiftLeft(n + 1 - k).multiply(Z.THREE.pow(k)).subtract(1);
  }
}
