package irvine.oeis.a191;
// manually a190803 at 2021-11-16

import irvine.math.z.Z;
import irvine.oeis.a190.A190803;

/**
 * A191283 Increasing sequence generated by these rules:  a(1)=1, and if x is in a then 2x and x(x+1)/2  are in a.
 * @author Georg Fischer
 */
public class A191283 extends A190803 {

  @Override
  public void insertTerms(final Z x) {
    insert(x.multiply(x.add(1)).divide2());
    insert(x.multiply2());
  }
}
